<script>
const BASE = "https://appdev.melda.io/api/rkb"

export default {
  name: 'HelloWorld',
  data() {
    return {
      isSearching: false,
      state: 'search-all',
      loading: false,
      packageInfo: {
        name: '',
        author: '',
        version: '',
        maintainer: '',
        needscompilation: '',
        description: ''
      },
      methodInfo: {
        name: '',
        alias: '',
        docType: '',
        title: '',
        details: '',
        references: '',
        Keyword: ''
      },
      packages: [],
      methods: [],
      authors: [],
      searchMethods: [],
      breadcrumb: [
        {
          name: 'Home',
          state: 'search-all'
        }
      ],
      page: 1,
      totalPages: 0,
      search: '',
      oldsearch: '',
    }
  },

  updated() {
    this.fixMeldaKbUrls()
  },
  
  methods: {
    fixMeldaKbUrls() {
      var selectors = [
        new RegExp('/method-detail'),
        new RegExp('/package-detail')
      ]

      var els

      if (this.$refs.main) {
        els = Array.prototype.slice.call(
          this.$refs.main.querySelectorAll('[href]')
        )

        els.forEach(el => {
          var url = el.href.toString()
          var relative = url.replace(
            window.location.origin.toString() + '/',
            ''
          )
          var match = false

          selectors.forEach(endpoint => {
            match = match || endpoint.test(url)
          })

          if (match) {
            el.href = window.KB_SERVER + relative
            el.target = '_blank'
          }
        })
      }
    },

    escapeText(text) {
      var span = document.createElement('span')
      span.innerHTML = text
      span.innerText = span.innerText.replace(/\\n|\n/g," ")   
      return span.innerText
    },

    replaceText(text){
      return text.replace(/\\n|\n/g," ")
    },

    setState(state) {
      this.state = state
      if (state == 'search-all')
        this.breadcrumb = this.breadcrumb.filter(p => p.state == 'search-all')
      else if (state == 'package-detail')
        this.breadcrumb = this.breadcrumb.filter(
          p => p.state == 'package-detail' || p.state == 'search-package-only' || 
          p.state == 'search-method-only' || p.state == 'search-all' ||
          p.state == 'search-author-only'
        )
      else if (state == "method-detail")
        this.breadcrumb = this.breadcrumb.filter(
          p => p.state == 'package-detail' || p.state == 'search-package-only' || 
          p.state == 'search-method-only' || p.state == 'search-all' ||
          p.state == 'search-author-only'
        )
    },

    showPackageMethods(name) {
      this.getPackageMethods(name)
    },


    showMethod(name) {
      this.getMethod(this.packageInfo.name, name)
    },

    searchHandler(searchIn) {
      this.isSearching = true
      switch ( searchIn ) {
      case "method":
        this.state = "search-method-only"
        break;
      case "package":
        this.state = "search-package-only"
        break;
      case "author":
        this.state = "search-author-only"
        break;
      default:
        this.state = "search-all"
        break;
      } 
      
      this.page = 1
      this.packages = []
      this.searchMethods = []
      this.searchIn = searchIn
      this.infiniteId += 1
    },

    getPackageMethods(name) {
      this.loading = true
      this.$http
        .get(BASE  + `/cran-package-detail?package=${name}`)
        .then(({ body }) => {
          this.methods = body.methods
          this.packageInfo = body.package
          this.state = 'package-detail'
          this.loading = false

          this.breadcrumb = [
            {
              state: 'search-all',
              name: 'Home'
            },
            {
              state: 'search-package-only',
              name: 'R Package List'
            },
            {
              state: 'package-detail',
              name
            }
          ]
        })
        .catch( () => {
          this.loading = false
        })
    },

    getMethod(packageName, method) {
      this.loading = true
      this.$http
        .get(BASE +`/cran-method-detail?package=${packageName}&method=${method}`)
        .then(({ body }) => {
          var breadcrumb = [
            {
              state: 'search-all',
              name: 'Home'
            },
            {
              state:'search-method-only',
              name: 'Method List'
            }
          ]
          if (body.package) {
            this.packageInfo = body.package

            breadcrumb.push({
              state: 'package-detail',
              name: this.packageInfo.name
            })
          }
          if (body.method) {
            this.methodInfo = body.method

            breadcrumb.push({
              state: 'method-detail',
              name: this.methodInfo.name
            })
          }

          this.state = 'method-detail'
          this.breadcrumb = breadcrumb
          this.loading = false
        })
        .catch(() => {
          this.loading = false
        })
    },

    searchAll(searchIn = "all", size = 3 ){
      this.isSearching = true
      this.page = 1
      this.loading = true
        this.$http
        .get(BASE + '/search?q=' + this.search + '&in=' + searchIn +'&size=' + size)
        .then( ({body}) => {
          this.searchMethods = body.methods;
          this.packages = body.packages;
          var breadcrumb = [
            {
              state: 'search-all',
              name: 'Home'
            }
          ]
          switch ( searchIn ) {
          case "all": 
            this.state = "search-all"
            break;
          case "method":
            this.state = "search-method-only"
            if(this.breadcrumb.length <= 1){
              console.log(this.breadcrumb.length)
              this.breadcrumb.push({
              state: 'search-method-only',
              name: 'R Method List'
            })
            }
            break;
          case "package":
            this.state = "search-package-only"
            if(this.breadcrumb.length <= 1){
              this.breadcrumb.push({
              state: 'search-package-only',
              name: 'R Package List'
            })
            }
            break;
          case "author":
            this.state = "search-author-only"
            if(this.breadcrumb.length <= 1){
              this.breadcrumb.push({
              state: 'search-author-only',
              name: 'Author List'
            })
            }
            break;  
          default:
            this.state = "search-all"
          }
          this.loading = false;
        })
        .catch(() => {
          this.loading = false
        })
    },
  }
}

</script>

